<?php
$template_dir = zen_get_template_dir();
DEFINE('SQL_SHOW_PRODUCT_INFO_MAIN',"select configuration_key,configuration_value from ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." where configuration_key RLIKE 'SHOW_PRODUCT_INFO_MAIN' and configuration_value > 0 and template_dir='$template_dir' UNION select distinct cfg.configuration_key, cfg.configuration_value from ".TABLE_CONFIGURATION." as cfg left join ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." as cfg_ft on cfg.configuration_key = cfg_ft.configuration_key and cfg_ft.template_dir='$template_dir' where cfg_ft.configuration_key IS NULL and cfg.configuration_key RLIKE 'SHOW_PRODUCT_INFO_MAIN' and cfg.configuration_value > 0 order by configuration_value");

DEFINE('SQL_SHOW_PRODUCT_INFO_CATEGORY', "select configuration_key,configuration_value from ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." where configuration_key RLIKE 'SHOW_PRODUCT_INFO_CATEGORY' and configuration_value > 0 and template_dir='$template_dir' UNION select distinct cfg.configuration_key, cfg.configuration_value from ".TABLE_CONFIGURATION." as cfg left join ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." as cfg_ft on cfg.configuration_key = cfg_ft.configuration_key and cfg_ft.template_dir='$template_dir' where cfg_ft.configuration_key IS NULL and cfg.configuration_key RLIKE 'SHOW_PRODUCT_INFO_CATEGORY' and cfg.configuration_value > 0 order by configuration_value");

DEFINE('SQL_SHOW_PRODUCT_INFO_LISTING_BELOW', "select configuration_key,configuration_value from ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." where configuration_key RLIKE 'SHOW_PRODUCT_INFO_LISTING_BELOW' and configuration_value > 0 and template_dir='$template_dir' UNION select distinct cfg.configuration_key, cfg.configuration_value from ".TABLE_CONFIGURATION." as cfg left join ".TABLE_CONFIGURATION_FOREACH_TEMPLATE." as cfg_ft on cfg.configuration_key = cfg_ft.configuration_key and cfg_ft.template_dir='$template_dir' where cfg_ft.configuration_key IS NULL and cfg.configuration_key RLIKE 'SHOW_PRODUCT_INFO_LISTING_BELOW' and cfg.configuration_value > 0 order by configuration_value");
?>
